linkGroup.innerContainer:
display: flex;align-items: flex-start;justify-content: space-between;flex-wrap: wrap;gap: 32px;font-size: 14px;font-weight: 500;
@media (max-width: 68.75rem){justify-content: revert;}
@media (max-width: 34.375rem){justify-content: space-between;}

imageStyle.container:
position: relative; border-radius: 50%; width: 400px; height: 400px; margin: 0 auto; overflow: revert;
&::before { content: ""; position: absolute; height: 100%; width: 100%; z-index: -1; border-radius: 50%; outline: 1px solid #000; outline-offset: 36px; };
&::after { content: ""; position: absolute; height: 40px; width: 40px; background-color: var(--primary-color); border-radius: 50%; top: 0%; left: 7%; box-shadow: 320px 344px var(--secondary-color); };
@media (max-width: 68.75rem) {
  width:400px; height: 400px;
  margin-top: 64px;
};
@media (max-width: 34.375rem) {
  width:300px; height: 300px;
  &::before { outline-offset: 24px; };
  &::after { height: 30px; width: 30px; left: 8%; box-shadow: 216px 274px var(--secondary-color); };
};

gridStyleConfig.gridWrapper:
& a:nth-child(even) > article > div {
  flex-direction: column;
  @media ,(max-width: 34.375rem), {
    flex-direction: column-reverse;
  }
}

--card-width: 50%;
& > a:first-child {
  grid-column: 1 / -1;
  & > article > div > picture {
    height: 330px;
    @media ,(max-width: 34.375rem), {
      height: 200px;
    }
  }
}

default.form:
grid-template-columns: repeat(2, 1fr);
grid-template-rows: repeat(3, 1fr);

& label:last-of-type {
  height: 100%;
  grid-column: -2 / 2;
  grid-row: -1 / 1;

  & textarea,
  & div {
    height: 100%;
  }

  @media (max-width: 34.375rem) {
    grid-column: revert;
    grid-row: revert;

    & textarea {
      height: 200px;
    }
  }
}

& button {
  grid-column: 1 / -1;
}
@media (max-width: 34.375rem) {
  grid-template-columns: revert;
  grid-template-rows: revert;
}

default.label:
border-color: hsla(var(--primary-color-raw-hsl) / 0.1);
background-color: hsl(0deg, 0%, 100%);
padding: 24px;

&:hover {
  border-color: var(--primary-color);
}

@media (max-width: 68.75rem) {
  padding: 16px;
}

navigation.navLink:
transition: color 300ms ease, background-color 300ms ease;
padding: 16px 24px;
border-radius: 40px;
@media (max-width: 68.75rem) {
  margin-left: 0;
  padding: 8px 16px;
  display: inline-block;
}

navigation.navListItem:
& > a {
  &:hover {
    color: var(--primary-color);
    background-color: hsl(var(--primary-color-raw-hsl) / 0.05);
  };
  &::before,&::after { content: revert; };
};
@media (max-width: 68.75rem) {
  margin-block: 8px;
};

navigation.phoneList:
margin-top: 10px;
& a {
  &:hover { color: var(--primary-color); }
  @media (max-width: 68.75rem) {
    &::after { content: revert; }
  }
}

